.carousel-bullet.active-bullet:hover {
  opacity: 1;
  transform: scale(1.3);
  background-color: var(--secondary-white);
  cursor: default;
}

.product-txt-container {
  display: flex;
  width: 100%;
  height: 100%;
  margin: 0 auto;
  flex-wrap: wrap;
  grid-row: 1 / span 2;
  align-items: center;
  justify-content: center;
  overflow-y: scroll;
}

.product-txt-wrapper {
  margin: 2.5% 10%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: 0.65rem;
}

.product-txt-wrapper > *:not(.product-txt) {
  margin-top: 2.5rem;
  margin-bottom: 2rem;
}

.product-txt-container::-webkit-scrollbar-track {
  background-color: #fff;
}

.product-txt-container::-webkit-scrollbar {
  width: 5px;
  background-color: #fff;
}

.product-txt-container::-webkit-scrollbar-thumb {
  background-color: var(--secondary-blk);
}

.product-txt-wrapper .product-txt-subhead {
  font-family: "Antonio", sans-serif;
  font-size: clamp(1rem, 9vw, 2rem);
  width: 95%;
  align-self: start;
  margin-bottom: 1rem;
  padding-bottom: 8px;
}

.product-txt {
  font-size: clamp(0.75rem, 10vw, 1rem);
  font-weight: lighter;
}

.product-txt h3 {
  font-weight: 500;
  margin-bottom: 0.5rem;
  padding: 0 !important;
}

.product-txt h4 {
  font-weight: 500;
  margin-bottom: 0.5rem;
}

.addtl_modal_body {
  margin-bottom: 1rem;
}

.addtl_modal_body_2 p {
  margin-bottom: 1rem;
}

.consultant_perks {
  font-weight: 100;
}

.product-txt .modal_block-header {
  font-weight: 500;
  margin-bottom: 0.25rem;
}

.recipes {
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
  margin: 0;
}

.recipes-header {
  display: inline-block;
  font-size: clamp(0.5rem, -0.875rem + 8.333333vw, 1.25rem);
  padding-bottom: 1rem;
  font-weight: 300;
}

.recipes-list {
  display: flex;
  padding: 0;
  flex: 1;
  flex-wrap: wrap;
  align-items: flex-start;
  justify-content: flex-start;
  flex-direction: row;
  width: 100%;
  height: 100%;
  position: relative;
}

.recipes-list li {
  display: flex;
  flex-direction: column;
  transition: all 0.25s ease;
  padding-bottom: 4px;
  max-width: 100%;
  margin: 0 1rem 0 0;
}

.recipe-link {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100%;
  width: 100%;
  text-align: center;
}

.recipes-list li:hover {
  transform: scale(1.04);
  cursor: pointer;
}

.recipes-list li .recipes-link {
  display: inline-block;
  text-align: center;
  width: 100%;
  height: 100%;
}

.recipe-preview-img-wrap {
  display: inline-block;
  display: flex;
  align-items: center;
  justify-content: center;
  background-size: cover;
  background-position: center;
  -o-object-fit: contain;
  object-fit: contain;
  margin-bottom: 10px;
  height: clamp(75px, -0.875rem + 8.333333vw, 200px);
  width: clamp(75px, -0.875rem + 8.333333vw, 200px);
  transition: box-shadow 0.3s ease;
}

.__related-content {
  width: 140px;
  height: 80px;
}

.recipes-list li:hover .recipe-preview-img-wrap {
  box-shadow: -5px 5px 6px -1px rgba(0, 0, 0, 0.4);
}

.recipes-list li:hover .recipe-title {
  font-weight: bold;
}

.recipe-title {
  padding: 0 1px;
  font-weight: 300;
  font-style: italic;
  text-align: center;
  transition: all 0.3s ease;
  width: clamp(75px, -0.875rem + 8.333333vw, 200px);
  margin-bottom: 1rem;
  height: 100%;
}


