  /* margin-left: 3px; */

  font-size: 12px;

  width: 100px;

  padding: 8px;

  border-radius: 5px;

  background-color: var(--accent-blue);

  color: var(--secondary-white);
  text-align: center;
  white-space: wrap;
  overflow: hidden;

  cursor: pointer;

  opacity: 0;
  transform: translateY(-8px);
  transition: transform 0.3s, opacity 0.3s;
