  display: flex;
  justify-content: flex-end;
  align-items: center;
}

#intro .intro-header .info-hotspot-close-wrapper {
  position: relative;
  align-self: center;
}

#intro-heading {
  display: flex;
  text-align: center;
  margin: 1rem 2rem;
}

#intro-heading .intro-h1 {
  margin: 0 1rem;
  font-weight: 500;
  max-width: 22ch;
  justify-self: flex-start;
}

#intro-body {
  display: flex;
  text-align: center;
  flex-direction: column;
  height: 100%;
  justify-content: center;
  padding: 0.5rem 0 2rem 0;
  max-width: 26ch;
}

#intro-buttons {
  display: flex;
  width: 100%;
  height: auto;
}

#intro-buttons .tour-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  border-top: 2px solid var(--secondary-blk);
  margin: 0;
  padding: 0;
  width: auto;
  overflow: visible;
  width: 100%;
  padding: 0.5rem;
  color: inherit;
  font: inherit;


  -webkit-font-smoothing: inherit;
  -moz-osx-font-smoothing: inherit;

  /* Corrects inability to style clickable `input` types in iOS */
  -webkit-appearance: none;
  flex: 1;
  transition: 0.1s background-color linear;
  text-align: center;
}

.tour-btn:hover {
  cursor: pointer;
  background-color: var(--accent-lightgray);
}

#intro-buttons .cancel {
  border-right: 2px solid var(--secondary-blk);
  border-bottom-left-radius: 0.5rem;
}

#intro-buttons .confirm {
  /* border-left: 1px solid var(--secondary-blk); */
