}

.loader-inner {
  margin-top: 3.5rem;
}

.loader-inner div {
  width: 10px;
  height: 10px;
  margin: 0px;
}

.preload-text {
  margin-top: 3.25rem;
  font-weight: lighter;
  font-size: 1rem;
}

.preload-text::after {
  content: " .";
  animation: dots 1s steps(5, end) infinite;
}

@keyframes dots {
  0%,
  20% {
    color: rgba(0, 0, 0, 0);
    text-shadow: 0.25em 0 0 rgba(0, 0, 0, 0), 0.5em 0 0 rgba(0, 0, 0, 0);
  }
  40% {
    color: white;
    text-shadow: 0.25em 0 0 rgba(0, 0, 0, 0), 0.5em 0 0 rgba(0, 0, 0, 0);
  }
  60% {
    text-shadow: 0.25em 0 0 white, 0.5em 0 0 rgba(0, 0, 0, 0);
  }
  80%,
  100% {
    text-shadow: 0.25em 0 0 white, 0.5em 0 0 white;
  }
}

.help-menu-btn {
  position: absolute;
  bottom: 0;
  right: 0;
  width: 100%;
  display: flex;
  padding: 1rem;
  justify-content: flex-end;
  opacity: 0;
  transition: opacity 0.4s ease-in;
}

.help-menu-btn svg {
  opacity: 0.75;
  transition: opacity 0.1s ease-in-out;
}

.help-menu-btn svg:hover {
  cursor: pointer;
  opacity: 1;
}

a,
a:hover,
a:active,
a:visited {
  text-decoration: none;
  color: inherit;
}

#pano {
  transition: opacity 0.4s ease-in;
  opacity: 0;
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
}

#titleBar {
  transition: opacity 0.4s ease-in;
  opacity: 0;
  display: flex;
  justify-content: flex-end;
  align-items: center;
  position: absolute;
  top: 0.5rem;
  left: 0;
  width: 100%;
  padding: 0 1rem;
}

body.multiple-scenes #titleBar {
  left: 0px;
}

body.multiple-scenes.mobile #titleBar {
  left: 0px;
}

.logoWrapper {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  width: 100%;
  padding-top: 5px;
  padding-bottom: 5px;
  margin-left: 10px;
}

.sceneName {
  display: block;
  max-width: 120px;
  height: auto;
}

#fullscreenToggle {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: 10px;
}

body.fullscreen-enabled #fullscreenToggle {
  display: block;
}

#fullscreenToggle .icon {
  max-width: 30px;
  max-height: 30px;
  -webkit-filter: invert(1);
  filter: invert(1);
  opacity: 0.8;
}

#fullscreenToggle .icon.on {
  display: none;
}

#fullscreenToggle .icon.off {
  display: block;
}

#fullscreenToggle.enabled .icon.on {
  display: block;
}

#fullscreenToggle.enabled .icon.off {
  display: none;
}

/* Link hotspot */

.link-hotspot {
  display: flex;
  margin-left: -30px;
  margin-top: -30px;
  opacity: 0.9;
  transition: opacity 0.2s;
}

.no-touch .link-hotspot:hover {
  opacity: 1;
}

.link-hotspot-icon {
  border: 2px solid var(--accent-blue);
  width: 30px;
  height: 30px;

  border-radius: 50%;
  cursor: pointer;
}

.link-hotspot-tooltip {
  position: absolute;
  align-self: center;
  left: -50%;
  top: 110%; /* ( 60 - (16 + 2*8) ) / 2 */

  /* margin-left: 3px; */

  font-size: 12px;

  width: 100px;

  padding: 8px;

  border-radius: 5px;

  background-color: var(--accent-blue);

  color: var(--secondary-white);
  text-align: center;
  white-space: wrap;
  overflow: hidden;

  cursor: pointer;

  opacity: 0;
  transform: translateY(-8px);
  transition: transform 0.3s, opacity 0.3s;
}

.no-touch .link-hotspot:hover .link-hotspot-tooltip {
  opacity: 1;
  transform: translateY(0);
}

/* Prevent tooltip from triggering */
.link-hotspot-tooltip {
  pointer-events: none;
}
.no-touch .link-hotspot:hover .link-hotspot-tooltip {
  pointer-events: all;
}

/* Fallback mode without pointer-events (IE8-10) */
.tooltip-fallback .link-hotspot-tooltip {
  display: none;
}
.no-touch .tooltip-fallback .link-hotspot:hover .link-hotspot-tooltip {
  display: block;
}

/* Info hotspot */
.info-hotspot .info-hotspot-content,
.info-hotspot .info-hotspot-close-wrapper {
  display: none;
}

.info-hotspot {
  line-height: 1.2em;
  opacity: 0.9;
  transition: opacity 0.2s 0.2s;
}

.info-hotspot:hover {
  opacity: 1;
  transition: opacity 0.2s;
}

.info-hotspot.visible {
  opacity: 1;
}

.info-hotspot .info-hotspot-header {
  width: 40px;
  height: 40px;
  border-radius: 20px;
  border: 2px solid var(--accent-green-opaque);
  cursor: pointer;
  transition: width 0.3s ease-in-out 0.5s, border-radius 0.3s ease-in-out 0.5s, background 1s ease-in-out;
}

.no-touch .info-hotspot .info-hotspot-header:hover {
  width: 210px;
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  transition: width 0.3s ease-in-out, border-radius 0.3s ease-in-out, background 0.5s ease-in-out;
  background: var(--accent-gray-opaque);
}

.no-touch .info-hotspot.visible .info-hotspot-header:hover {
  width: 210px;
  background: var(--secondary-white-opaque);

  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
  transition: width 0.3s ease-in-out, border-radius 0.3s ease-in-out, background 0.3s ease-in-out,
    background 0.5s ease-in-out;
}

.info-hotspot .info-hotspot-icon-wrapper {
  width: 40px;
  height: 40px;
}

.info-hotspot .info-hotspot-icon {
  max-width: 80%;
  height: 80%;
  margin: 1.5px;
}

.info-hotspot .info-hotspot-title-wrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  left: 42px;
  top: 0;
  width: 0;
  height: 40px;
  padding: 0;
  overflow: hidden;
  transition: width 0s 0.4s, padding 0s 0.4s;
}

.no-touch .info-hotspot .info-hotspot-header:hover .info-hotspot-title-wrapper {
  width: 160px;
  padding: 0 0 0 5px;
  transition: width 0s 0.4s, padding 0s 0.4s;
}

.info-hotspot .info-hotspot-title-wrapper:before {
  content: "";
  display: inline-block;
  vertical-align: middle;
  height: 100%;
}

.info-hotspot .info-hotspot-title {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  height: 100%;
  padding: 0;
  margin: 0;
  vertical-align: middle;
  font-size: 0.8rem;
  line-height: 0.8rem;
  -moz-user-select: text;
  -webkit-user-select: text;
  -ms-user-select: text;
  user-select: text;
}

/* media queries */
@media (min-width: 1440px) {
  .link-hotspot-icon {
    width: 60px;
    height: 60px;
  }
  .link-hotspot-tooltip {
    right: -80%;
    font-size: 1rem;
    width: 170px;
  }
  #fullscreenToggle .icon {
    width: 24px;
    height: 24px;
    -webkit-filter: invert(1);
    filter: invert(1);
  }
  #titleBar {
    height: 70px;
  }
  #titleBar .sceneName {
    max-width: 150px;
  }
}
